    INSERT INTO website_why_us_cards (title, description, icon, icon_url, display_order, is_active) VALUES
    ('Preston''s Most Advanced Student Hub', 'The biggest, boldest upgrade to student life in the city.', NULL, NULL, 0, true),
    ('Your Own City Apartment', 'Live independently while surrounded by like-minded people and lifestyle-driven spaces.', NULL, NULL, 1, true),
    ('Tesco Express Right Downstairs', 'Everyday essentials just seconds away convenience at your doorstep.', NULL, NULL, 2, true),
    ('All-in-one Living with 10+ Lifestyle Zones', 'Gym, cinema, rooftop terrace, chill lounges, study zones all under one roof.', NULL, NULL, 3, true),
    ('Exclusive Rooftop Terrace For Residents Only', 'The only student property in Preston with private rooftop access and skyline views.', NULL, NULL, 4, true),
    ('Trusted by 400+ Students Every Year', 'The go-to choice for students who want more than just a place to sleep.', NULL, NULL, 5, true),
    ('Designed with a Modern Touch', 'Every space features sleek, high-quality furnishings and contemporary design.', NULL, NULL, 6, true),
    ('Fitness-Focused Living', 'Train just steps from your room wellness and motivation built in.', NULL, NULL, 7, true);
